import React, { Component } from "react";
import { connect } from "react-redux";
import * as actions from "./actions/actions";
import "./App.css";
import Container from "react-bootstrap/Container";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/form";
import Modal from "react-bootstrap/Modal";
import swal from "sweetalert";
import Nav from "react-bootstrap/Nav";
import { useHistory } from "react-router-dom";
const responseGoogle = (response) => {
  console.log(response);
};

class Registration extends React.Component {
  constructor() {
    super();
    this.state = {
      status: false,
      updatePopup: false,
      insertPopup: false,
    };
  }

  componentDidMount() {
    this.props.getProducts();
  }

  showPopup = (msg) => {
    if (msg === "update") {
      this.setState({
        status: true,
        updatePopup: true,
        insertPopup: false,
      });
    }

    if (msg === "insert") {
      this.setState({
        status: true,
        insertPopup: true,
        updatePopup: false,
      });
    }
  };
  deleteProduct = (p_id) => {
    this.props.deleteProduct(p_id);
  };

  closePopup = () => {
    this.setState({
      status: false,
      insertPopup: false,
      updatePopup: false,
    });
  };

  save = () => {
    if (this.state.updatePopup) {
      this.props.updateProduct({
        p_id: this.refs.p_id.value,
        p_name: this.refs.p_name.value,
        p_cost: this.refs.p_cost.value,
      });
    } else if (this.state.insertPopup) {
      if (this.validate()) {
        this.props.addProduct({
          name: this.refs.name.value,
          email: this.refs.email.value,
          password: this.refs.password.value,
          gender: this.refs.male.value,
          gender: this.refs.female.value,
          city: this.refs.city.value,
          partner: this.refs.partner.value,
          mobile: this.refs.mobile.value,
          budget: this.refs.budget.value,
          wedding: this.refs.wedding.value,
        });
        this.closePopup();
      }
    }
  };

  validate() {
    let isValid = true;

    if (!this.refs.name.value) {
      alert("Invalid Name");
      return false;
    }

    if (!this.refs.email.value) {
      alert("Invalid email");
      return false;
    }

    if (this.refs.email.value !== "undefined") {
      var pattern = new RegExp(
        /^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i
      );
      if (!pattern.test(this.refs.email.value)) {
        alert("Invalid email");
        return false;
      }
    }

    if (!this.refs.city.value) {
      alert("Invalid City");
      return false;
    }

    if (!this.refs.mobile.value) {
      alert("Invalid Mobile");
      return false;
    }

    if (!this.refs.budget.value) {
      alert("Invalid Budget");
      return false;
    }

    if (!this.refs.wedding.value) {
      alert("Invalid Wedding Date");
      return false;
    }

    return isValid;
  }

  render() {
    return (
      <Container fluid id="pic">
        <img src={require("./img/logo1.png")} className="img" />
        <Nav.Link
          href="/Home"
          style={{ display: "inline", color: "black", fontSize: 20 }}
        >
          HOME
        </Nav.Link>
        <Button
          className="mt-4 btn-lg btn-success float-right"
          style={{ fontSize: 25 }}
          onClick={() => {
            this.showPopup("insert");
          }}
        >
          Register Here
        </Button>

        <Modal
